using System;\nusing System.Collections.Generic;\nusing System.Linq;\nusing System.Windows.Forms;\n\npublic class CustomControl : Control\n{\n    public DateTime DateProperty { get; set; }\n}\n\npublic partial class YourForm : Form\n{\n    private List<CustomControl> controlsList = new List<CustomControl>();\n\n    public YourForm()\n    {\n        InitializeComponent();\n        flowLayoutPanel1.ControlAdded += FlowLayoutPanel1_ControlAdded;\n    }\n\n    private void FlowLayoutPanel1_ControlAdded(object sender, ControlEventArgs e)\n    {\n        if (e.Control is CustomControl customControl)\n        {\n            controlsList.Add(customControl);\n            SortControls();\n        }\n    }\n\n    private void SortControls()\n    {\n        var sortedControls = controlsList.OrderBy(c => c.DateProperty).ToList();\n\n        flowLayoutPanel1.SuspendLayout(); // Prevent redrawing the layout during sorting\n        flowLayoutPanel1.Controls.Clear();\n\n        foreach (var control in sortedControls)\n        {\n            flowLayoutPanel1.Controls.Add(control);\n        }\n\n        flowLayoutPanel1.ResumeLayout(true); // Redraw the layout after sorting is complete\n    }\n}