using DinkToPdf;\nusing DinkToPdf.Contracts;\n\npublic class PdfConverter\n{\n    private readonly IConverter _converter;\n\n    public PdfConverter(IConverter converter)\n    {\n        _converter = converter;\n    }\n\n    public byte[] ConvertUrlToPdf(string url)\n    {\n        var globalSettings = new GlobalSettings\n        {\n            ColorMode = ColorMode.Color,\n            Orientation = Orientation.Portrait,\n            PaperSize = PaperKind.A4Plus,\n        };\n\n        var objectSettings = new ObjectSettings\n        {\n            PagesCount = true,\n            HtmlContent = $"<!DOCTYPE html><html><head></head><body>{url}</body></html>",\n            WebSettings = { DefaultEncoding = "utf-8", EnableIntelligentShrinking = true },\n            HeaderSettings = { FontSize = 9, Right = "Page [page] of [toPage]", Line = false, Spacing = 2.812 }\n        };\n\n        var doc = new HtmlToPdfDocument()\n        {\n            GlobalSettings = globalSettings,\n            Objects = { objectSettings }\n        };\n        \n        return _converter.Convert(doc);\n    }\n}