using System;\nusing System.IO;\nusing System.Security.Cryptography;\n\npublic class SecureTransmission {\n    public static void Main() {\n        string filePath = "example.txt";\n        byte[] fileData = File.ReadAllBytes(filePath);\n\n        // Sign the data\n        using (RSACryptoServiceProvider rsa = new RSACryptoServiceProvider(2048)) {\n            string privateKeyXml = rsa.ToXmlString(true);  // Keep this safe!\n            string publicKeyXml = rsa.ToXmlString(false);\n            \n            byte[] signature = rsa.SignData(fileData, HashAlgorithmName.SHA256, RSASignaturePadding.Pkcs1);\n\n            // Encrypt the data and signature using recipient's public key\n            using (RSACryptoServiceProvider recipientRsa = new RSACryptoServiceProvider()) {\n                recipientRsa.FromXmlString(publicKeyXml);  // Use actual recipient's public key\n\n                byte[] encryptedData = recipientRsa.Encrypt(fileData, true);\n                byte[] encryptedSignature = recipientRsa.Encrypt(signature, true);\n\n                // Send encryptedData and encryptedSignature to the recipient\n            }\n        }\n    }\n}