XmlSchema schema = XmlSchema.Read(schemaStream, null);\nXmlReaderSettings xmlReaderSettings = new XmlReaderSettings();\nxmlReaderSettings.Schemas.Add(schema.TargetNamespace, schema); // Ensure the correct namespace is used\nxmlReaderSettings.ValidationType = ValidationType.Schema;\nxmlReaderSettings.ValidationFlags |= XmlSchemaValidationFlags.ReportValidationWarnings; // Reports warnings as well\nxmlReaderSettings.ValidationEventHandler += ValidationEventHandler;\n\nXmlReader xmlReader = XmlReader.Create(xmlStream, xmlReaderSettings);\nwhile (xmlReader.Read())\n{\n}\n\nprivate void ValidationEventHandler(object sender, ValidationEventArgs args)\n{\n    if (args.Severity == XmlSeverityType.Warning)\n    {\n        Console.WriteLine("Warning: " + args.Message);\n    }\n    else // Severity is Error\n    {\n        Console.WriteLine("Error: " + args.Message);\n        _IsValid = false;\n    }\n}