using System;\nusing System.Collections.Generic;\nusing System.Diagnostics;\nusing System.Linq;\n\npublic static class EventLogExtensions\n{\n    public static IEnumerable<EventLogEntry> EventLogEntriesSince(this string logName, DateTime startDate, List<string> sourcesToIgnore = null)\n    {\n        using (EventLog eventLog = new EventLog(logName))\n        {\n            sourcesToIgnore ??= new List<string>(); // Default to empty list if null\n\n            return eventLog.Entries.Cast<EventLogEntry>()\n                .Where(entry => entry.TimeGenerated >= startDate && !sourcesToIgnore.Contains(entry.Source));\n        }\n    }\n}