using System.Data.SqlClient;\n\npublic void BulkInsertData(string connectionString, string filePath)\n{\n    using (SqlConnection connection = new SqlConnection(connectionString))\n    {\n        connection.Open();\n        using (SqlBulkCopy bulkCopy = new SqlBulkCopy(connection,\n            SqlBulkCopyOptions.TableLock | SqlBulkCopyOptions.FireTriggers))\n        {\n            // Set the destination table name\n            bulkCopy.DestinationTableName = "YourDestinationTable";\n\n            // Optional: Assign column mappings if needed\n            // bulkCopy.ColumnMappings.Add("SourceColumn", "DestinationColumn");\n\n            using (var reader = new StreamReader(filePath))\n            {\n                using (var csv = new CsvReader(reader))  // Assuming you are using a library like CsvHelper for reading CSV files\n                {\n                    bulkCopy.BatchSize = 5000;  // Adjust batch size as needed\n                    bulkCopy.BulkCopyTimeout = 600;  // Timeout in seconds\n\n                    bulkCopy.WriteToServer(csv);\n                }\n            }\n        }\n    }\n}