using System;\nusing System.Diagnostics;\n\nclass Program\n{\n    static void Main()\n    {\n        string recipientEmail = "recipient@example.com"; // Change this to the recipient's email as per their public key in your keyring\n        string inputFile = @"C:\path\to\your\file.txt";\n        string outputFile = @"C:\path\to\your\encryptedfile.gpg";\n\n        var processInfo = new ProcessStartInfo\n        {\n            FileName = "gpg.exe",\n            Arguments = $"--encrypt --recipient \"{recipientEmail}\" --output \"{outputFile}\" \"{inputFile}\"",\n            RedirectStandardOutput = true,\n            UseShellExecute = false,\n            CreateNoWindow = true\n        };\n\n        var process = new Process();\n        process.StartInfo = processInfo;\n        process.Start();\n        string output = process.StandardOutput.ReadToEnd();\n        process.WaitForExit();\n\n        Console.WriteLine("Encryption complete. Output: ");\n        Console.WriteLine(output);\n    }\n}