using System;\nusing System.Xml;\nusing System.Xml.XPath;\n\npublic class NamespaceHandler\n{\n    public static void Main()\n    {\n        string xmlContent = @"<Elements name='Entities' xmlns='XS-GenerationToolElements'>\n                                <Element>Value</Element>\n                              </Elements>";\n\n        XmlDocument doc = new XmlDocument();\n        doc.LoadXml(xmlContent);\n\n        XPathNavigator navigator = doc.CreateNavigator();\n\n        // Get default namespace\n        XmlNamespaceManager manager = new XmlNamespaceManager(navigator.NameTable);\n        string defaultNs = doc.DocumentElement.GetAttribute("xmlns");\n        if (!string.IsNullOrWhiteSpace(defaultNs))\n        {\n            manager.AddNamespace("ns", defaultNs);\n\n            // Use the namespace in your XPath queries\n            XPathExpression expr = navigator.Compile("//ns:Elements");\n            expr.SetContext(manager);\n\n            XPathNodeIterator nodes = navigator.Select(expr);\n            while (nodes.MoveNext())\n            {\n                Console.WriteLine(nodes.Current.OuterXml);\n            }\n        }\n    }\n}