string hql = "SELECT e FROM EventItem e " +\n             "WHERE e.AudienceList like :audienceIdPattern1 or " +\n                   "e.AudienceList like :audienceIdPattern2 or " +\n                   "e.AudienceList like :audienceIdPattern3";\n\nvar audienceIdString = myAudience.ID.ToString();\nvar results = SessionInstance.Session.CreateQuery(hql)\n               .SetParameter("audienceIdPattern1", "," + audienceIdString + ",")\n               .SetParameter("audienceIdPattern2", audienceIdString + ",")\n               .SetParameter("audienceIdPattern3", "," + audienceIdString)\n               .List<EventItem>();