using System;\nusing System.Diagnostics;\n\npublic class MapLauncher\n{\n    public static void ShowMap(string address)\n    {\n        // Encode the address for URL use\n        string encodedAddress = Uri.EscapeUriString(address);\n        \n        // Create a Google Maps URL with the encoded address\n        string mapUrl = $"http://maps.google.com/maps?q={encodedAddress}";\n        \n        // Start Internet Explorer Mobile and navigate to the map\n        Process.Start("iexplore.exe", mapUrl);\n    }\n}