using System;\nusing System.Threading;\n\nclass Program\n{\n    static void Main(string[] args)\n    {\n        // Use a unique name for the Mutex, using application ID or something similar.\n        string mutexName = "YourUniqueMutexName";\n        bool createdNew;\n\n        // Attempt to open the named mutex. If it does not exist, create one.\n        using (var mutex = new Mutex(true, mutexName, out createdNew))\n        {\n            if (!createdNew)\n            {\n                Console.WriteLine("Another instance is already running.");\n                return;  // Exit because another instance is already running.\n            }\n\n            try\n            {\n                // Your application logic here.\n                Console.WriteLine("Application is running.");\n                // Simulate some work with a sleep.\n                Thread.Sleep(5000);\n            }\n            finally\n            {\n                mutex.ReleaseMutex();\n            }\n        }\n    }\n}