internal void RefCursorDataReader()\n{\n    using (OracleConnection conn = new OracleConnection(_constr))\n    {\n        using (OracleCommand cmd = new OracleCommand("ABC$MYPACKAGE.MyProc", conn))\n        {\n            cmd.CommandType = CommandType.StoredProcedure;\n            conn.Open();\n            cmd.BindByName = true;\n\n            OracleParameter p = cmd.Parameters.Add("parameter_no1", OracleDbType.Int32);\n            p.Value = 12345678;\n            p.Direction = ParameterDirection.Input;\n\n            p = cmd.Parameters.Add("parameter_no2", OracleDbType.Int32);\n            p.Value = 123456;\n            p.Direction = ParameterDirection.Input;\n\n            p = cmd.Parameters.Add("RETCURSOR", OracleDbType.RefCursor);\n            p.Direction = ParameterDirection.Output;\n\n            using (OracleDataReader reader = cmd.ExecuteReader())\n            {\n                while (reader.Read())\n                {\n                    System.Diagnostics.Debug.WriteLine(reader[0].GetType().ToString());\n                }\n            }\n        }\n    }\n}