using System;\nusing System.Drawing;\n\npublic class ColorConverter {\n    public static int ConvertColorToArgbInt(Color color) {\n        // The format is AARRGGBB, where each component is 1 byte in size.\n        // Combine them into an integer:\n        return (color.A << 24) | (color.R << 16) | (color.G << 8) | color.B;\n    }\n\n    public static void Main() {\n        ColorDialog colordialog1 = new ColorDialog();\n        if (colordialog1.ShowDialog() == System.Windows.Forms.DialogResult.OK) {\n            int argbInt = ConvertColorToArgbInt(colordialog1.Color);\n            Console.WriteLine("The ARGB integer is: " + argbInt);\n        }\n    }\n}