public class CaseNoteService {\n    private readonly CaseNoteDal _caseNoteDal;\n\n    public CaseNoteService() {\n        _caseNoteDal = new CaseNoteDal();  // Dependency Injection would be better practice in production code\n    }\n\n    public List<CaseNote> GetCaseNotesForMember(string memberId) => _caseNoteDal.GetAllByMember(memberId);\n\n    public void AddNewNote(CaseNote note) => _caseNoteDal.Insert(note);\n}