public class CustomersModule : IModule\n{\n    private readonly IUnityContainer _container;\n\n    public CustomersModule(IUnityContainer container)\n    {\n        _container = container;\n    }\n\n    public void OnInitialized(IContainerProvider containerProvider)\n    {\n        // No additional initialization is needed here for view/viewmodel registration.\n    }\n\n    public void RegisterTypes(IContainerRegistry containerRegistry)\n    {\n        // Register customer view in the DI container\n        containerRegistry.RegisterForNavigation<CustomerAllView>();\n\n        // Register the viewmodels that rely on views.\n        containerRegistry.RegisterType<CustomerAllViewModel>();\n    }\n}