return Fluently.Configure().Database(\n    MsSqlConfiguration.MsSql2005\n        .ConnectionString(c => c\n            .TrustedConnection()\n            .Server("localhost")\n            .Database("LDTT")))\n    .Mappings(m => m.FluentMappings.AddFromAssemblyOf<UserMap>())\n    .Mappings(m => m.FluentMappings.AddFromAssemblyOf<AnotherMappingInDifferentAssembly>()) // Include other assemblies here\n    .ExposeConfiguration(BuildSchema)\n    .BuildSessionFactory();