using Microsoft.Win32;\nusing System;\n\nclass Program\n{\n    static void Main()\n    {\n        string edgePath = GetMicrosoftEdgePath();\n        if (!string.IsNullOrEmpty(edgePath))\n        {\n            Console.WriteLine("Microsoft Edge Path: " + edgePath);\n        }\n        else\n        {\n            Console.WriteLine("Microsoft Edge not found.");\n        }\n    }\n\n    private static string GetMicrosoftEdgePath()\n    {\n        try\n        {\n            const string EdgeKey = @"SOFTWARE\Microsoft\Windows NT\CurrentVersion\App Paths\msedge.exe";\n            using (RegistryKey edgePath = Registry.LocalMachine.OpenSubKey(EdgeKey, false))\n            {\n                if (edgePath != null)\n                {\n                    return edgePath.GetValue("") as string;\n                }\n            }\n        }\n        catch (Exception ex)\n        {\n            Console.WriteLine("An error occurred: " + ex.Message);\n        }\n\n        return null;\n    }\n}