using Microsoft.Win32;\nusing System;\n\nclass Program\n{\n    static void Main()\n    {\n        string iePath = GetInternetExplorerPath();\n        if (!string.IsNullOrEmpty(iePath))\n        {\n            Console.WriteLine("Internet Explorer Path: " + iePath);\n        }\n        else\n        {\n            Console.WriteLine("Internet Explorer not found.");\n        }\n    }\n\n    private static string GetInternetExplorerPath()\n    {\n        try\n        {\n            const string IeKey = @"SOFTWARE\Microsoft\Windows\CurrentVersion\App Paths\iexplore.exe";\n            using (RegistryKey iePath = Registry.LocalMachine.OpenSubKey(IeKey, false))\n            {\n                if (iePath != null)\n                {\n                    return iePath.GetValue("") as string;\n                }\n            }\n        }\n        catch (Exception ex)\n        {\n            Console.WriteLine("An error occurred: " + ex.Message);\n        }\n\n        return null;\n    }\n}