using System;\nusing System.Data;\nusing System.Data.OracleClient;\nusing System.Configuration;\n\npublic void FetchJDETime()\n{\n    try\n    {\n        string ora = ConfigurationManager.ConnectionStrings["OracleConnString"].ConnectionString;\n\n        using (OracleConnection con = new OracleConnection(ora))\n        using (OracleCommand cmd = new OracleCommand("SELECT timestamp FROM jde_out WHERE rownum = 1", con))\n        {\n            cmd.CommandType = CommandType.Text;\n            con.Open();\n\n            object result = cmd.ExecuteScalar();\n            if (result != null)\n            {\n                this.lblJDEtime.Text = result.ToString();\n            }\n        }\n    }\n    catch (OracleException ex)\n    {\n        // Log the exception details securely\n        LogException(ex);\n\n        // Display a generic error message to the user\n        MessageBox.Show("An error occurred while fetching data.");\n    }\n}\n\nprivate void LogException(Exception ex)\n{\n    // Implement your logging logic here\n}