using System;\nusing System.IO;\nusing System.Security.AccessControl;\n\npublic static void SetPermissions(string dir)\n{\n    DirectoryInfo info = new DirectoryInfo(dir);\n    DirectorySecurity ds = info.GetAccessControl();\n\n    FileSystemRights rights = FileSystemRights.FullControl;\n    InheritanceFlags inheritanceFlags = InheritanceFlags.ContainerInherit | InheritanceFlags.ObjectInherit;\n    PropagationFlags propagationFlags = PropagationFlags.NoPropagateInherit; // or use PropagationFlags.None if you want the changes to be inherited\n    AccessControlType accessControlType = AccessControlType.Allow;\n\n    ds.AddAccessRule(new FileSystemAccessRule(@"BUILTIN\Users", rights, inheritanceFlags, propagationFlags, accessControlType));\n\n    info.SetAccessControl(ds);\n}