using System.IO;\nusing iTextSharp.text.pdf;\n\npublic void ReorderPdfPages(string sourcePath, string destPath, int[] newOrder)\n{\n    // Open the existing document to read\n    PdfReader reader = new PdfReader(sourcePath);\n    \n    using (FileStream fs = new FileStream(destPath, FileMode.Create))\n    {\n        // Create a new document with the same size and margins as the original\n        Document outputDocument = new Document(reader.GetPageSizeWithRotation(1));\n        \n        // Initialize the copy object for copying pages\n        PdfCopy copy = new PdfCopy(outputDocument, fs);\n        \n        outputDocument.Open();\n        \n        // Loop through the provided order array to add pages in the desired order\n        foreach (int pageNumber in newOrder)\n        {\n            copy.AddPage(copy.GetImportedPage(reader, pageNumber));\n        }\n        \n        outputDocument.Close();\n    }\n    \n    reader.Close();\n}