using System;\nusing System.Runtime.InteropServices;\n\npublic class UnregisteredComObjectExample\n{\n    [DllImport("ole32.dll", CharSet = CharSet.Unicode)]\n    private static extern int LoadLibraryEx(string lpFileName, IntPtr hFile, uint dwFlags);\n\n    public static void Main()\n    {\n        // Path to your OCX file\n        string ocxPath = @"C:\path\to\your.ocx";\n\n        // Load the OCX file into memory\n        int hModule = LoadLibraryEx(ocxPath, IntPtr.Zero, 0);\n        \n        if (hModule == 0)\n            throw new Exception("Failed to load OCX");\n\n        try {\n            // CLSID of your ActiveX control\n            Guid clsid = new Guid("your-clsid-here");\n            \n            // Create an instance of the COM object.\n            Type type = Type.GetTypeFromCLSID(clsid);\n            if (type == null)\n                throw new Exception("Failed to get type from CLSID");\n\n            dynamic comObject = Activator.CreateInstance(type);\n\n            // Use the COM object here...\n        }catch(Exception ex) {\n            Console.WriteLine(ex.Message);\n        }\n    }\n}