using System;\nusing System.Net.Sockets;\n\nUdpClient udpClient = new UdpClient(12346); // Listening on port 12346\nIPEndPoint RemoteIpEndPoint = new IPEndPoint(IPAddress.Any, 0);\n\nbyte[] receivedBytes = udpClient.Receive(ref RemoteIpEndPoint);\n\n// Convert bytes back to int if you know it's an int and little-endian\nint receivedNumber = BitConverter.ToInt32(receivedBytes, 0); // Assumes data starts at index 0\n\n// Convert bytes back to string\nstring receivedMessage = Encoding.UTF8.GetString(receivedBytes);