private void DrawAndClearRectangle(Graphics graphics, Pen pen, Rectangle rect)\n{\n    // Save the area under the rectangle to a bitmap\n    Bitmap background = new Bitmap(rect.Width, rect.Height);\n    using (Graphics g = Graphics.FromImage(background))\n    {\n        g.CopyFromScreen(this.PointToScreen(new Point(rect.Left, rect.Top)), Point.Empty, new Size(rect.Width, rect.Height));\n    }\n\n    // Draw the rectangle\n    graphics.DrawRectangle(pen, rect);\n    Application.DoEvents(); // Ensure UI updates\n    System.Threading.Thread.Sleep(75);\n\n    // Clear the rectangle by redrawing the saved background\n    using (Graphics g = this.CreateGraphics())\n    {\n        g.DrawImage(background, rect.Location);\n    }\n}