using System;\nusing System.ServiceModel;\n\npublic class MyServiceHost\n{\n    public static void Main()\n    {\n        Uri baseAddress = new Uri("net.tcp://localhost:808/MyService");\n        \n        // Create the ServiceHost\n        using (ServiceHost host = new ServiceHost(typeof(MyService), baseAddress))\n        {\n            // Add a service endpoint with NetTcpBinding  \n            NetTcpBinding binding = new NetTcpBinding();\n            binding.Security.Mode = SecurityMode.None; // For security reasons, consider a more secure mode in production.\n\n            host.AddServiceEndpoint(\n                typeof(IMyService),\n                binding,\n                "MyService");\n\n            try\n            {\n                // Open the ServiceHostBase to start listening for messages.\n                host.Open();\n\n                Console.WriteLine("The service is ready at {0}", baseAddress);\n                Console.WriteLine("Press <ENTER> to stop the service.");\n                Console.ReadLine();\n                \n                // Close the ServiceHostBase to release resources.\n                host.Close();\n            }\n            catch (CommunicationException ce)\n            {\n                Console.WriteLine("An exception occurred: {0}", ce.Message);\n                host.Abort();\n            }\n        } \n    }\n}