using System;\nusing System.Text.RegularExpressions;\nusing System.Web.UI;\nusing System.Web.UI.WebControls;\n\npublic partial class _Default : Page\n{\n    protected void Page_Load(object sender, EventArgs e)\n    {\n        string userContent = "Check out this link http://example.com and also visit https://sample.org!";\n        string safeHtmlContent = ConvertUrlsToLinks(userContent);\n        \n        // Render the HTML content with URLs as links opening in new tabs\n        LiteralControl.Controls.Add(new Literal { Text = safeHtmlContent });\n    }\n\n    private string ConvertUrlsToLinks(string input)\n    {\n        // Regex pattern to match URLs\n        string pattern = @"(http[s]?://[^\s""']+)";\n        return Regex.Replace(input, pattern, m => $"<a href=\"{m.Groups[0].Value}\" target=\"_blank\">{m.Groups[0].Value}</a>");\n    }\n}