using System;\nusing System.Web;\n\nnamespace HandlerLib\n{\n    public class testhandler : IHttpHandler\n    {\n        public void ProcessRequest(HttpContext context)\n        {\n            // Extract the requested path\n            string requestPath = context.Request.AppRelativeCurrentExecutionFilePath.Substring(2); // Remove leading "~/"\n            \n            // Split on "/" to get segments\n            string[] segments = requestPath.Split(new char[] { '/' }, StringSplitOptions.RemoveEmptyEntries);\n            \n            if (segments.Length > 1 && segments[0].ToLower() == "testhandler")\n            {\n                string input = segments.Length > 1 ? segments[1] : null;\n                \n                if (!string.IsNullOrEmpty(input))\n                {\n                    context.Response.ContentType = "text/plain";\n                    context.Response.Write($"Your Input Was: {input}");\n                }\n                else\n                {\n                    context.Response.StatusCode = 400; // Bad Request if no input provided\n                    context.Response.Write("No input specified.");\n                }\n            }\n            else\n            {\n                context.Response.StatusCode = 404; // Not Found if URL format does not match expected pattern\n                context.Response.Write("Resource not found.");\n            }\n        }\n\n        public bool IsReusable => false;\n    }\n}