// Initial number as a string\nstring numberAsString = "010";\n\n// Convert to integer for arithmetic operation\nint numberAsInt = int.Parse(numberAsString);\n\n// Add 1 to the number\nnumberAsInt += 1;\n\n// Format back to a string with leading zeros, preserving the original length\nstring result = numberAsString.Length > numberAsInt.ToString().Length\n                ? numberAsInt.ToString(new string('0', numberAsString.Length))\n                : numberAsInt.ToString();\n\nConsole.WriteLine(result); // Output: "011"