using System;\nusing System.Xml;\n\nclass Program\n{\n    static void Main()\n    {\n        XmlDocument xmlDoc = new XmlDocument();\n        // Assume xmlDoc is loaded with your XML data\n        \n        XPathNavigator xPathNavigator = xmlDoc.CreateNavigator();\n        XmlNamespaceManager xmlNamespaceManager = new XmlNamespaceManager(xPathNavigator.NameTable);\n        string xpath = "//YourXPathHere"; // Replace with your actual XPath\n\n        XPathNavigator node = xPathNavigator.SelectSingleNode(xpath, xmlNamespaceManager);\n\n        if (node != null)\n        {\n            using (XmlWriter writer = node.AppendChild())\n            {\n                writer.WriteStartElement("Property", xmlDoc.DocumentElement.NamespaceURI);\n                writer.WriteAttributeString("name", "someName");\n                writer.WriteAttributeString("type", "String");\n                writer.WriteCData("someValue");\n                writer.WriteEndElement();\n            }\n        }\n\n        xmlDoc.Save("output.xml"); // Save the modified XML to a file or another output\n    }\n}