using System;\nusing System.Net.Mail;\nusing System.Text.RegularExpressions;\n\npublic void SendEmail(string userEmail, string userMessage)\n{\n    // Validate email format (basic example, consider more robust validation if necessary)\n    const string pattern = @"^\S+@\S+\.\S+$";\n    bool isValidEmail = Regex.IsMatch(userEmail, pattern);\n    \n    if (!isValidEmail) \n    {\n        throw new ArgumentException("Invalid email address.");\n    }\n\n    using (MailMessage mail = new MailMessage())\n    {\n        // Always hardcode the recipient and cc/bcc addresses\n        mail.From = new MailAddress("your-email@example.com");\n        mail.To.Add(new MailAddress("recipient@example.com"));\n\n        // Ensure subject is safe by limiting length and characters if necessary\n        if (string.IsNullOrEmpty(userMessage) || userMessage.Length > 200)\n        {\n            throw new ArgumentException("Invalid message.");\n        }\n        \n        mail.Subject = "User Feedback"; \n        mail.Body = userMessage;\n\n        using (SmtpClient smtp = new SmtpClient("smtp.example.com", 587))\n        {\n            smtp.Credentials = new System.Net.NetworkCredential("username", "password");\n            smtp.EnableSsl = true;\n            try\n            {\n                smtp.Send(mail);\n            }\n            catch (Exception ex)\n            {\n                // Handle exception \n                throw;\n            }\n        }\n    }\n}