// Example of saving an image to the file system and storing the path in SQL Server\nusing System;\nusing System.IO;\nusing System.Data.SqlClient;\n\npublic void SaveImageToFileSystemAndDB(string imagePath, string fileName)\n{\n    // Define file paths\n    string basePath = @"C:\Images";\n    string fullPath = Path.Combine(basePath, fileName);\n\n    // Copy or move image to the target directory\n    File.Copy(imagePath, fullPath, true);\n\n    // Store the path in SQL Server\n    using (SqlConnection conn = new SqlConnection("YourConnectionString"))\n    {\n        conn.Open();\n        using (SqlCommand cmd = conn.CreateCommand())\n        {\n            cmd.CommandText = "INSERT INTO Images (FilePath) VALUES (@FilePath)";\n            cmd.Parameters.AddWithValue("@FilePath", fullPath);\n            cmd.ExecuteNonQuery();\n        }\n    }\n}