static void Main(string[] args)\n{\n    long n = 600851475143;\n    long largestPrime = -1;\n\n    // Divide out the factor of 2 first\n    while (n % 2 == 0)\n    {\n        largestPrime = 2;\n        n /= 2;\n    }\n\n    // Check odd numbers starting from 3\n    for (long i = 3; i <= Math.Sqrt(n); i += 2)\n    {\n        while (n % i == 0)\n        {\n            largestPrime = i;\n            n /= i;\n        }\n    }\n\n    // If n becomes a prime number greater than 2\n    if (n > 2)\n    {\n        largestPrime = n;\n    }\n\n    Console.WriteLine("The largest prime factor is " + largestPrime.ToString() + ".");\n    Console.ReadLine();\n}