using System;\nusing System.Diagnostics;\nusing System.Threading;\n\nclass Program\n{\n    static void Main()\n    {\n        string processName = "notepad"; // Replace with the target process name\n        TimeSpan interval = TimeSpan.FromSeconds(30); // Set the monitoring interval\n\n        while (true)\n        {\n            if (IsProcessRunning(processName))\n            {\n                Console.WriteLine($"{processName} is running. Initializing the rest of the program...");\n                // Add code to initialize the rest of your program here\n\n                break; // Exit loop after initializing, or continue monitoring?\n            }\n            else\n            {\n                Console.WriteLine($"{processName} is not running.");\n            }\n\n            Thread.Sleep(interval); // Wait for the specified interval before checking again\n        }\n    }\n\n    static bool IsProcessRunning(string processName)\n    {\n        foreach (var process in Process.GetProcesses())\n        {\n            if (string.Equals(process.ProcessName, processName, StringComparison.OrdinalIgnoreCase))\n            {\n                return true;\n            }\n        }\n\n        return false;\n    }\n}