// Define StringHolder Entity\npublic class StringHolder\n{\n    public virtual int Id { get; set; }\n    public virtual string Type { get; set; } // e.g., "CITY", "FOOBAR"\n    public virtual string Value { get; set; }\n}\n\n// Repository or Service method to retrieve specific types of strings\npublic IEnumerable<StringHolder> GetStringsByType(string type)\n{\n    using (var session = sessionFactory.OpenSession())\n    {\n        return session.Query<StringHolder>().Where(sh => sh.Type == type).ToList();\n    }\n}