using System;\nusing System.Net;\nusing System.Net.Mail;\n\npublic partial class _Default : System.Web.UI.Page\n{\n    protected void Page_Load(object sender, EventArgs e)\n    {\n        try\n        {\n            SendEmail("info@thedomain.com", "jcarrascal@gmail.com", "testing...", "Hello, world.com");\n        }\n        catch (Exception ex)\n        {\n            // Log the exception details securely\n            // For example, log to event viewer\n            System.Diagnostics.EventLog.WriteEntry("SMTP Error: " + ex.Message);\n        }\n    }\n\n    private void SendEmail(string from, string to, string subject, string body)\n    {\n        using (var client = new SmtpClient("localhost"))\n        {\n            using (var mailMessage = new MailMessage(from, to, subject, body))\n            {\n                client.Send(mailMessage);\n            }\n        }\n    }\n}