using System;\nusing System.Data.SqlClient;\n\npublic void InsertTime(int id, string dayName, DateTime timeFrom, DateTime timeTo)\n{\n    // Base date to combine with time values\n    DateTime baseDate = new DateTime(1900, 1, 1);\n\n    // Combine base date with time values\n    DateTime dateTimeFrom = baseDate.Date.Add(timeFrom.TimeOfDay);\n    DateTime dateTimeTo = baseDate.Date.Add(timeTo.TimeOfDay);\n\n    string connectionString = "YourConnectionStringHere";\n    using (SqlConnection connection = new SqlConnection(connectionString))\n    {\n        string query = @"\n            INSERT INTO dbo.Availability (id, dayName, timeFrom, timeTo)\n            VALUES (@id, @dayName, @dateTimeFrom, @dateTimeTo)";\n\n        SqlCommand command = new SqlCommand(query, connection);\n        command.Parameters.AddWithValue("@id", id);\n        command.Parameters.AddWithValue("@dayName", dayName);\n        command.Parameters.AddWithValue("@dateTimeFrom", dateTimeFrom);\n        command.Parameters.AddWithValue("@dateTimeTo", dateTimeTo);\n\n        connection.Open();\n        command.ExecuteNonQuery();\n    }\n}