private void StreamPDFReport(string ReportPath, HttpContext context)\n{\n    context.Response.Buffer = false;\n    context.Response.Clear();\n    context.Response.ClearContent();\n    context.Response.ClearHeaders();\n\n    // Set the appropriate ContentType.\n    context.Response.ContentType = "application/pdf";\n    context.Response.AddHeader("Content-Disposition", "inline; filename=Report.pdf");\n    context.Response.Cache.SetCacheability(HttpCacheability.NoCache);\n    context.Response.Cache.SetNoStore();\n    context.Response.Cache.SetMaxAge(TimeSpan.Zero);\n\n    // Add Vary header\n    context.Response.AppendHeader("Vary", "*");\n\n    using (FileStream sourceStream = new FileStream(ReportPath, FileMode.Open))\n    {\n        long fileLength = sourceStream.Length;\n        byte[] buffer = new byte[4096];\n\n        int bytesRead;\n        while ((bytesRead = sourceStream.Read(buffer, 0, buffer.Length)) > 0)\n        {\n            context.Response.OutputStream.Write(buffer, 0, bytesRead);\n        }\n    }\n\n    HttpContext.Current.ApplicationInstance.CompleteRequest();\n}