using Microsoft.Practices.EnterpriseLibrary.Data;\nusing System.Data;\n\npublic void CallStoredProcedureWithOleDb()\n{\n    Database db = DatabaseFactory.CreateDatabase("YourConnectionStringName");\n    \n    DbCommand command = db.GetStoredProcCommand("YourStoredProcedureName");\n\n    // Add parameters to the stored procedure\n    db.AddInParameter(command, "Parameter1", DbType.Int32, yourIntegerValue);\n    db.AddInParameter(command, "Parameter2", DbType.String, yourStringValue);\n    // Continue adding parameters as needed...\n\n    // No result set is expected in this case\n    db.ExecuteNonQuery(command);\n}