// Data Layer: Database specific classes\npublic class CustomerDataAccess\n{\n    private readonly string _connectionString;\n\n    public CustomerDataAccess(string connectionString)\n    {\n        _connectionString = connectionString;\n    }\n\n    public DataTable GetAllCustomers()\n    {\n        using (SqlConnection connection = new SqlConnection(_connectionString))\n        {\n            SqlDataAdapter adapter = new SqlDataAdapter("SELECT * FROM Customers", connection);\n            DataTable dataTable = new DataTable();\n            adapter.Fill(dataTable);\n            return dataTable;\n        }\n    }\n}\n\n// Business Layer: Process data and convert to business objects\npublic class CustomerBusinessLogic\n{\n    private readonly CustomerDataAccess _dataAccess;\n\n    public CustomerBusinessLogic(CustomerDataAccess dataAccess)\n    {\n        _dataAccess = dataAccess;\n    }\n\n    public List<Customer> GetCustomers()\n    {\n        DataTable dataTable = _dataAccess.GetAllCustomers();\n        List<Customer> customers = new List<Customer>();\n\n        foreach (DataRow row in dataTable.Rows)\n        {\n            Customer customer = new Customer\n            {\n                Id = (int)row["Id"],\n                Name = row["Name"].ToString(),\n                Email = row["Email"].ToString()\n            };\n            customers.Add(customer);\n        }\n\n        return customers;\n    }\n}\n\n// Business Object: Represents a customer entity\npublic class Customer\n{\n    public int Id { get; set; }\n    public string Name { get; set; }\n    public string Email { get; set; }\n}