using FluentValidation;\n\npublic class DataFeedValidator : AbstractValidator<DataFeed>\n{\n    public DataFeedValidator()\n    {\n        RuleFor(df => df.ImportDate).Must(HaveHappenedRecently);\n        RuleFor(df => df.RowCount).InclusiveBetween(1, 100000);\n        RuleForEach(df => df.Rows).ChildRules(row =>\n        {\n            row.RuleFor(x => x.Values).NotEqual(null);\n        });\n    }\n\n    private bool HaveHappenedRecently(DateTime date)\n    {\n        return DateTime.Now - date <= TimeSpan.FromDays(7);  // Example rule\n    }\n}