using System;\nusing System.Security.Cryptography;\nusing System.Text;\n\npublic class SecureEncryptionExample {\n    public static void Main(string[] args) {\n        // Create a new triple DES instance.\n        TripleDESCryptoServiceProvider cryptoProvider = new TripleDESCryptoServiceProvider();\n\n        // Generate symmetric key for 3DES.\n        byte[] symmetricKey = cryptoProvider.Key;\n\n        // Load the certificate from file.\n        X509Certificate2 cert = new X509Certificate2(@"c:\temp\whatever.cer");\n\n        // Get public RSA key.\n        RSACryptoServiceProvider rsaPublic = (RSACryptoServiceProvider)cert.PublicKey.Key;\n\n        // Encrypt the symmetric key using the public RSA key.\n        byte[] encryptedSymmetricKey = rsaPublic.Encrypt(symmetricKey, true);\n\n        Console.WriteLine("Encrypted Symmetric Key: " + Convert.ToBase64String(encryptedSymmetricKey));\n    }\n}