using System.Data.SqlClient;\n\npublic class SqlCommandExtractorForAdapter\n{\n    public static void LogCommands(SqlDataAdapter dataAdapter)\n    {\n        // Accessing SqlCommand objects from a SqlDataAdapter.\n        if (dataAdapter.SelectCommand != null)\n            Console.WriteLine("Select Command: " + dataAdapter.SelectCommand.CommandText);\n\n        if (dataAdapter.InsertCommand != null)\n            Console.WriteLine("Insert Command: " + dataAdapter.InsertCommand.CommandText);\n\n        if (dataAdapter.UpdateCommand != null)\n            Console.WriteLine("Update Command: " + dataAdapter.UpdateCommand.CommandText);\n\n        if (dataAdapter.DeleteCommand != null)\n            Console.WriteLine("Delete Command: " + dataAdapter.DeleteCommand.CommandText);\n    }\n}