using System;\nusing System.IO;\nusing System.ServiceModel;\nusing System.ServiceModel.Web;\n\n[ServiceContract]\npublic interface IExcelDocumentService\n{\n    [OperationContract]\n    [WebGet(ResponseFormat = WebMessageFormat.Raw)]\n    Stream GetExcelFile(int documentId);\n}\n\npublic class ExcelDocumentService : IExcelDocumentService\n{\n    public Stream GetExcelFile(int documentId)\n    {\n        byte[] excelBytes = FetchExcelFromDatabase(documentId); // Implement this method to get bytes from your DB\n\n        if (excelBytes == null || excelBytes.Length == 0)\n        {\n            WebOperationContext.Current.OutgoingResponse.StatusCode = System.Net.HttpStatusCode.NotFound;\n            return Stream.Null;\n        }\n\n        WebOperationContext.Current.OutgoingResponse.ContentType = "application/vnd.openxmlformats-officedocument.spreadsheetml.sheet";\n        return new MemoryStream(excelBytes);\n    }\n\n    private byte[] FetchExcelFromDatabase(int documentId)\n    {\n        // Implement your database logic to fetch the Excel file by ID\n        // Return the bytes of the Excel file\n        throw new NotImplementedException();\n    }\n}