using System.Net.Http;\nusing System.Web.Mvc;\n\npublic class SomeController : Controller\n{\n    [HttpPost]\n    public ActionResult SubmitForm(FormCollection form)\n    {\n        var recaptchaResponse = Request["g-recaptcha-response"];\n        var secretKey = "your_secret_key"; // Keep this private\n\n        var client = new HttpClient();\n        var values = new Dictionary<string, string>\n        {\n            { "secret", secretKey },\n            { "response", recaptchaResponse }\n        };\n\n        var content = new FormUrlEncodedContent(values);\n        var response = await client.PostAsync("https://www.google.com/recaptcha/api/siteverify", content);\n        var resultString = await response.Content.ReadAsStringAsync();\n        dynamic responseObj = JsonConvert.DeserializeObject(resultString);\n\n        if (responseObj.success)\n        {\n            // Proceed with form processing\n        }\n        else\n        {\n            // CAPTCHA validation failed, handle accordingly\n        }\n\n        return View();\n    }\n}