using System;\nusing System.Collections.Generic;\n\npublic class Program\n{\n    public static List<int> GetAllIndexes(string str, string value)\n    {\n        List<int> indexes = new List<int>();\n        int index = 0;\n        \n        while ((index = str.IndexOf(value, index)) != -1)\n        {\n            indexes.Add(index);\n            index++; // Move to the next character after the current match\n        }\n        \n        return indexes;\n    }\n\n    public static void Main()\n    {\n        string sourceString = "This is a simple test string for testing.";\n        string searchString = "is";\n        \n        List<int> indexes = GetAllIndexes(sourceString, searchString);\n        \n        Console.WriteLine("Indexes of substring '{0}':", searchString);\n        foreach (int index in indexes)\n        {\n            Console.WriteLine(index);\n        }\n    }\n}