using System;\nusing System.Runtime.InteropServices;\n\nnamespace MyManagedDll\n{\n    public static class HelloWorld\n    {\n        // Exported method\n        [DllImport("kernel32.dll", CharSet = CharSet.Unicode, SetLastError = true)]\n        public static extern int MessageBox(IntPtr hWnd, string text, string caption, uint type);\n\n        // Method to be called from unmanaged code\n        [ComVisible(true)]\n        [Guid("F935DC22-1CF0-11D0-ADB9-00C04FD58A0B")]\n        public static class ExportedMethods\n        {\n            [DllExport]\n            public static void ShowHelloWorld()\n            {\n                MessageBox(IntPtr.Zero, "Hello from C# DLL!", "Greetings", 0);\n            }\n        }\n    }\n}