using System;\n\n[AttributeUsage(AttributeTargets.All)]\npublic class SomeAttribute : Attribute\n{\n    private readonly string foreignIdentificator;\n\n    public SomeAttribute(string foreignIdentificator)\n    {\n        if (!Guid.TryParse(foreignIdentificator, out _))\n        {\n            throw new ArgumentException("Invalid GUID format", nameof(foreignIdentificator));\n        }\n        \n        this.foreignIdentificator = foreignIdentificator;\n    }\n\n    public Guid ForeignIdentificatorGuid => new Guid(this.foreignIdentificator);\n}