public static class ConfigManager\n{\n    private static readonly object _initLock = new object();\n    private static volatile ConfigurationData _config;\n\n    public static ConfigurationData Config\n    {\n        get \n        {\n            if (_config == null)\n            {\n                lock (_initLock)\n                {\n                    if (_config == null)\n                    {\n                        _config = InitializeConfig(); // Method to initialize config\n                    }\n                }\n            }\n            return _config;\n        }\n    }\n\n    private static ConfigurationData InitializeConfig()\n    {\n        // Logic to load and configure your data here\n        return new ConfigurationData(/* parameters */);\n    }\n}\n\npublic class ConfigurationData\n{\n    public readonly int SomeSetting;\n\n    public ConfigurationData(int someSetting)\n    {\n        SomeSetting = someSetting;\n    }\n}