using System;\nusing System.Globalization;\n\npublic class DateComparison\n{\n    public static void Main()\n    {\n        // Example user input date as string\n        string date_input = "03-apr-2023"; // This should be replaced with actual input\n        \n        // Parse the date_input to a DateTime object\n        DateTime parsedDate;\n        if (DateTime.TryParseExact(date_input, "dd-MMM-yyyy", CultureInfo.InvariantCulture,\n                                   DateTimeStyles.None, out parsedDate))\n        {\n            // Get the date one year ago from today\n            DateTime oneYearAgo = DateTime.Now.AddYears(-1);\n\n            // Compare dates\n            if (parsedDate >= oneYearAgo)\n            {\n                Console.WriteLine("The entered date is within the last 12 months.");\n            }\n            else\n            {\n                Console.WriteLine("The entered date is more than 12 months ago.");\n            }\n        }\n        else\n        {\n            Console.WriteLine("Invalid date format. Please use dd-MMM-yyyy.");\n        }\n    }\n}