using System;\nusing System.Collections.Generic;\n\npublic class ArticleEntity\n{\n    public string Text { get; set; }\n}\n\npublic static class ListHelper\n{\n    private static readonly List<ArticleEntity> list = new List<ArticleEntity>\n    {\n        new ArticleEntity { Text = "AAA" },\n        new ArticleEntity { Text = "AAB" },\n        new ArticleEntity { Text = "AAC" },\n        new ArticleEntity { Text = "ACC" },\n        new ArticleEntity { Text = "ADA" }\n    };\n\n    public static int FindPositionForArticle(string word)\n    {\n        string key = word.ToLower();\n        for (int i = 0; i < list.Count; i++)\n        {\n            if (string.Compare(list[i].Text.ToLower(), key, StringComparison.Ordinal) > 0)\n            {\n                return i;\n            }\n        }\n        return list.Count; // If all elements are less than the new word, add it at the end\n    }\n\n    public static void Main()\n    {\n        string newWord = "ABB";\n        int position = FindPositionForArticle(newWord);\n        Console.WriteLine($"The best position to insert '{newWord}' is: {position}");\n    }\n}